mapper.select:
SELECT count(*)
  FROM cart
 WHERE uNo = #{uNo}
   AND pNo = #{pNo}
   AND cStatus = 'C'

mapper.update:
UPDATE cart
   SET cCount = #{cCount}
 WHERE cNo = #{cNo}
   AND cStatus = 'C'

UPDATE cart
   SET cStatus = 'O'
     , oNo = #{oNo}
 WHERE uNo = #{uNo}
   AND cStatus = 'C'
