mapper.update:
UPDATE Notice
SET uNo = #{uNo}, nTitle = #{nTitle}, nContents = #{nContents}
WHERE nNo = #{nNo}

UPDATE Notice 
SET delYN = 'Y'
WHERE nNo = #{nNo}

mapper.select:
SELECT *
, (SELECT CONCAT ( SUBSTRING(uName,1,1),RPAD('',CHAR_LENGTH(uName)-1,'*') ) FROM User WHERE uNo=qna.uNo )AS uName
,( SELECT uStatus FROM User WHERE uNo = Qna.uNo ) AS uStatus FROM Qna WHERE delYN = 'N' AND qNo = #{qNo}

mapper.update:
UPDATE Qna 
SET delYN = 'Y'
WHERE qNo = #{qNo}

mapper.select:
SELECT count(*) as cnt FROM User 
WHERE uNo = #{uNo} 
AND uStatu = 'A'

mapper.update:
UPDATE Notice 
SET delYN = 'Y'
WHERE nNo = #{nNo}
