mapper.insert:
INSERT INTO
product (
	 pType
	,pName
	,pPrice
	,pInput
	,pOutput
	,pStock
) VALUES (
	 #{pType}
	,#{pName}
	,#{pPrice}
	,#{pInput}
	,0
	,#{pInput}
)

	SELECT MAX(pNo)
	  FROM product

mapper.update:
UPDATE product
   SET pInput = #{pInput}
     , pStock = pStock+#{pInput}
 WHERE pNo = #{pNo}

UPDATE product
   SET pOutput = #{pOutput}
     , pStock = pStock-#{pOutput}
 WHERE pNo = #{pNo}

UPDATE product
   SET pType = #{pType}
     , pName = #{pName}
     , pPrice = #{pPrice}
     , pDate = now()
 WHERE pNo = #{pNo}
