mapper.select:
SELECT *
     , (SELECT uName FROM user WHERE review.uNo = user.uNo) as uName
  FROM review
 WHERE 1=1

   AND uNo = #{searchValue}


   AND pNo = #{searchValue}

 LIMIT #{startNum}, #{perPageNum}

mapper.insert:
INSERT INTO
review (
	uNo,
	rContents,
	pNo
) VALUES (
	#{uNo},
	#{rContents},
	#{pNo}
)

mapper.update:
UPDATE review
   SET rContents = #{rContents}
 WHERE rNo = #{rNo}

UPDATE review
   SET rDelyn = 'Y'
 WHERE uNo = #{uNo}
   AND pNo = #{pNo}

UPDATE review
   SET rDelyn = 'Y'
 WHERE rNo = #{rNo}
